import React, { Component } from 'react'
import api from '../../services/api'
import { Link } from 'react-router-dom'

import './styles.css'

export default class Main extends Component {
   //variaveis de estado que ouve alteracoes
    state = {
       details: [],
       detailsInfo: {},
       page: 1
   }

    //exec assim que o componente e mostrado
    componentDidMount(){
        this.loadDetails()
    }

    prevPage = () => {

        const { page } = this.state

        if(page === 1) return

        const pageNumber = page - 1

        this.loadDetails(pageNumber)

    }

    nextPage = () => {
        const { page, detailsInfo } = this.state

        if(page === detailsInfo.pages) return //.pages eh o limite de paginas

        const pageNumber = page + 1

        this.loadDetails(pageNumber)

    }

    createDetails = async () => {

        const response = await api.post('/products', {title: 'oi', description: 'oi', url: 'www'})

    }

    loadDetails = async (page = 1) => { 
        const response = await api.get(`/products?page=${page}`)

        console.log(response.info)

        const { docs, ...detailsInfo} = response.data;

