import React, { Component } from 'react'
import { Link, useHistory } from 'react-router-dom'
import { FiMail, FiTrash } from 'react-icons/fi'
import { toast } from 'react-toastify'

import history from '../../services/history'
import api from '../../services/api'

import './styles.css'

export default class Details extends Component {
    state ={
        details: {}
    }

    async componentDidMount(){

        const { id } = this.props.match.params

        const response = await api.get(`/projects/${id}`)

        this.setState({ details: response.data })

    }

    deleteDetails = async (e) => {

        e.preventDefault()

        console.log('oi')


        const { id } = this.props.match.params


        const response = await api.delete(`/projects/${id}`)

        console.log(response)
        history.push('/')

        return response

        /* response.then( res => {
            console.log('oi2')

            console.log(res)
        }) */

        /* toast.success('Deletado com Sucesso')
        history.push('/') */

    }

    render(){
        const { details } = this.state

        return ( 
        <div className='details-info'>
            <p className='title'>Detalhes do Projeto</p>
            <img src={details.img}/>
            <h1>{details.name}</h1>
            <p>{details.description}</p>

            <div className='buttons'>
                <a href={details.url}><FiMail size={16} color='orange'/></a>
                <div></div>
                <button className='button-delete' onClick={this.deleteDetails}>
                    <FiTrash size={32} color='orange'/>
                    </button>
                <Link className='button-back' to='/'>Voltar</Link>
            </div>
        </div> 
        )}
}
