import React, { Component } from 'react'
import api from '../../services/api'
import { Link } from 'react-router-dom'

import './styles.css'

export default class Main extends Component {
   //variaveis de estado que ouve alteracoes
    state = {
       products: [],
       productInfo: {},
       page: 1
   }

    //exec assim que o componente e mostrado
    componentDidMount(){
        this.loadProducts()
    }

    prevPage = () => {

        const { page } = this.state

        if(page === 1) return

        const pageNumber = page - 1

        this.loadProducts(pageNumber)

    }

    nextPage = () => {
        const { page, productInfo } = this.state

        if(page === productInfo.pages) return //.pages eh o limite de paginas

        const pageNumber = page + 1

        this.loadProducts(pageNumber)

    }

    //criando produto padrao
    createProduct = async () => {

        const response = await api.post('/products', {title: 'oi', description: 'oi', url: 'www'})

    }

    //arrow function para func nossas e nao do ReactJS
    loadProducts = async (page = 1) => { 
        const response = await api.get(`/products?page=${page}`)

        console.log(response.data)

        const { docs, ...productInfo} = response.data;

       this.setState({ products: docs, productInfo, page }) //productInfo: productInfo
    }

    render(){
        const { products, productInfo, page } = this.state
        //{chave} sao substituidas por (parenteses)
        //o key e sempre pedido pelo React
        return(
            <div className='product-list'>
                {products.map(product =>(
                    <article key={product._id}>
                        <strong>{product.title}</strong>
                        <p>{product.description}</p>

                        <Link to={`products/${product._id}`}>Acessar</Link>
                    </article>
                ))}
                <div className='actions'>
                    <button 
                    disabled={page === 1} 
                    onClick={this.prevPage}>
                        Back</button>
                    <button 
                    onClick={this.createProduct}>
                        Add</button>
                    <button 
                    disabled={page === productInfo.pages} 
                    onClick={this.nextPage}>
                        Next</button>
                </div>
            </div>
        )
    }
}
