import React, { Component } from 'react'
import { Link, useHistory } from 'react-router-dom'
import { toast } from 'react-toastify'
import { FiArrowLeft } from 'react-icons/fi'


import history from '../../services/history'
import api from '../../services/api'

import './styles.css'

export default class Regiter extends Component {
    state ={
        name: "",
        description: "",
        img: "",
        url: ""
    }

    handleNewProject = async (e) => {
        e.preventDefault()

        try{
            await api.post('/register', this.state)
            history.push('/')
        } catch(err){
            alert('Erro ao cadastrar caso, tente novamente.')
        }
    }

    render(){

        return ( 

            <div className="content">
               <section>

                   <h1>Cadastrar novo Projeto</h1>
                   <p></p>

                   <Link className='button-back' to='/'>Voltar</Link>
               </section>
               <form onSubmit={this.handleNewProject}>
                   <input  
                       placeholder="Título do caso"
                       value={this.state.name}
                       onChange={e => this.setState({name: e.target.value})}
                   />

                   <textarea   
                       placeholder="Descrição"
                       value={this.state.description}
                       onChange={e => this.setState({description: e.target.value})}
                   />
                     <textarea   
                       placeholder="Imagem "
                       value={this.state.img}
                       onChange={e => this.setState({img: e.target.value})}
                   />
                   <input  
                       placeholder="Link do Projeto"
                       value={this.state.url}
                       onChange={e => this.setState({url: e.target.value})}
                   />

                   <button className="button" type="submit">Cadastrar</button>
               </form>
          </div>

        )}
}
