import React, { Component } from 'react'
import api from '../../services/api'
import { Link } from 'react-router-dom'

import './styles.css'

export default class Main extends Component {
   //variaveis de estado que ouve alteracoes
    state = {
       details: [],
       detailsInfo: {},
       page: 1
   }

    //exec assim que o componente e mostrado
    componentDidMount(){
        this.loadDetails()
    }

    prevPage = () => {

        const { page } = this.state

        if(page === 1) return

        const pageNumber = page - 1

        this.loadDetails(pageNumber)

    }

    nextPage = () => {
        const { page, detailsInfo } = this.state

        if(page === detailsInfo.pages) return //.pages eh o limite de paginas

        const pageNumber = page + 1

        this.loadDetails(pageNumber)

    }

    createDetails = async () => {

        const response = await api.post('/', {
            name: "Novo projeto2",
            description:"Projeto voltado a TI",
            img: "https://blog.cedrotech.com/wp-content/uploads/2016/01/quais-as-metricas-utilizadas-para-viabilizar-um-projeto-de-ti.jpg",
            url: "www.google.com.br"
        })

    }

    loadDetails = async (page = 1) => { 
        const response = await api.get('/projects') //`/projects?page=${page}`

        console.log(response)

        const { docs, ...detailsInfo } = response.data;

       this.setState({ details: docs, detailsInfo, page })

    }

    render(){
        const { details, page, detailsInfo } = this.state

        return(

             <div className='details-list'>
                   {details.map(detail =>(
                   <article>
                       <img src={detail.img}/>
                        <strong>{detail.name}</strong>
                        <p>{detail.description}</p>
                        <p>{detail.url}</p>

                       <Link to={`projects/${detail._id}`}>Detalhes</Link> 
                    </article>
                    ))}
                <div className='actions'>
                    <button 
                    disabled={page === 1} 
                    onClick={this.prevPage}>
                        Back</button>
                    <button 
                    onClick={this.createDetails}>
                        Add</button>
                    <button 
                    disabled={page === detailsInfo.pages} 
                    onClick={this.nextPage}>
                        Next</button>
                </div>
            </div>
        )
    }
}
