import React, { Component } from 'react'
import api from '../../services/api'
import { Link } from 'react-router-dom'

import './styles.css'

export default class Main extends Component {
   //variaveis de estado que ouve alteracoes
    state = {
       details: {},
       detailsInfo: {},
       page: 1
   }

    //exec assim que o componente e mostrado
    componentDidMount(){
        this.loadDetails()
    }

    prevPage = () => {

        const { page } = this.state

        if(page === 1) return

        const pageNumber = page - 1

        this.loadDetails(pageNumber)

    }

    nextPage = () => {
        const { page, detailsInfo } = this.state

        if(page === detailsInfo.pages) return //.pages eh o limite de paginas

        const pageNumber = page + 1

        this.loadDetails(pageNumber)

    }

    createDetails = async () => {

        const response = await api.post('/', {name: 'oi', schema: 'oi', url: 'www'})

    }

    loadDetails = async (page = 1) => { 
        const response = await api.get('/projects')//?page=${page}

        //console.log(response.data.info)

        const { info } = response.data;

    //    console.log(info)

       this.setState({ details: info })

    }

    render(){
        const { details, page, detailsInfo } = this.state

        console.log(details)

 /*       const resposta = details.map(d =>{
         return d.name
       })

       console.log(resposta) */

        return(

             <div className='details-list'>
                   <article>
                        <strong>{details.name}</strong>
                        <span></span>
                        <p>{details.description}</p>
                        <p>{details.schema}</p>

                       <Link to={`#/${details._postman_id}`}>Acessar</Link> 
                    </article>
                <div className='actions'>
                    <button 
                    disabled={page === 1} 
                    onClick={this.prevPage}>
                        Back</button>
                    <button 
                    onClick={this.createDetails}>
                        Add</button>
                    <button 
                    disabled={page === detailsInfo.pages} 
                    onClick={this.nextPage}>
                        Next</button>
                </div>
            </div>
        )
    }
}
