import React, { Component } from 'react'
import { Link, useHistory } from 'react-router-dom'
import { toast } from 'react-toastify'

//import history from '../../services/history'
import api from '../../services/api'

import './styles.css'

export default class Details extends Component {
    state ={
        details: {},
        history : useHistory()
    }


    async componentDidMount(){

        const { id } = this.props.match.params

        const response = await api.get(`/projects/${id}`)

        this.setState({ details: response.data })

    }

    deleteDetails = async () => {

        toast.success('Deletado com sucesso')

        const { id } = this.props.match.params

        const response = await api.delete(`/projects/${id}`, (req, res) => {
            toast.success('Deletado com sucesso')
            history.push('/')
            console.log(res)
        })

        /* toast.success('Deletado com Sucesso')
        history.push('/') */

        return response
    }

    render(){
        const { details } = this.state

        return ( 
        <div className='details-info'>
            <img src={details.img}/>
            <h1>{details.name}</h1>
            <p>{details.description}</p>
            <p>
                URL: <a href={details.url}>{details.url}</a>
            </p>
            <div className='buttons'>
                <button className='button-delete' onClick={this.deleteDetails}>Deletar</button>
                <div></div>
                 <Link className='button-back' to='/'>Voltar</Link>
            </div>
        </div> 
        )}
}
