import React, { Component } from 'react'
import api from '../../services/api'
import { Link } from 'react-router-dom'

import './styles.css'

export default class Main extends Component {
   //variaveis de estado que ouve alteracoes
    state = {
       details: [],
       detailsInfo: {},
       page: 1
   }

    //exec assim que o componente e mostrado
    componentDidMount(){
        this.loadDetails()
    }

    prevPage = () => {

        const { page } = this.state

        if(page === 1) return

        const pageNumber = page - 1

        this.loadDetails(pageNumber)

    }

    nextPage = () => {
        const { page, detailsInfo } = this.state

        if(page === detailsInfo.pages) return //.pages eh o limite de paginas

        const pageNumber = page + 1

        this.loadDetails(pageNumber)

    }

    createDetails = async () => {

        const response = await api.post('/products', {title: 'oi', description: 'oi', url: 'www'})

    }

    loadDetails = async (page = 1) => { 
        const response = await api.get(`/products?page=${page}`)

        console.log(response.data)

        const { docs, ...detailsInfo} = response.data;

       this.setState({ details: docs, detailsInfo, page })
    }

    render(){
        const { details, detailsInfo, page } = this.state

        return(
            <div className='details-list'>
                {details.map(detail =>(
                    <article key={detail._id}>
                        <strong>{detail.title}</strong>
                        <p>{detail.description}</p>

                        <Link to={`products/${details._id}`}>Acessar</Link>
                    </article>
                ))}
                <div className='actions'>
                    <button 
                    disabled={page === 1} 
                    onClick={this.prevPage}>
                        Back</button>
                    <button 
                    onClick={this.createDetails}>
                        Add</button>
                    <button 
                    disabled={page === detailsInfo.pages} 
                    onClick={this.nextPage}>
                        Next</button>
                </div>
            </div>
        )
    }
}
