import React, { Component } from 'react'
import api from '../../services/api'

import './styles.css'

export default class Main extends Component {
   //variaveis de estado que ouve alteracoes
    state = {
       products: [],
       productInfo: {},
   }

    //exec assim que o componente e mostrado
    componentDidMount(){
        this.loadProducts()
    }

    prevPage = () => {

    }

    nextPage = () => {
        const { page, productInfo } = this.state

        if(page === productInfo.pages) returrn //se
    }

    //arrow function para func nossas e nao do ReactJS
    loadProducts = async () => { 
        const response = await api.get('/products')
        console.log(response.data)

        const { docs, ...productInfo} = response.data;

       this.setState({ products: docs, productInfo}) //productInfo: productInfo
    }

    render(){
        const { products } = this.state
        //{chave} sao substituidas por (parenteses)
        //o key e sempre pedido pelo React
        return(
            <div className='product-list'>
                {products.map(product =>(
                    <article key={product._id}>
                        <strong>{product.title}</strong>
                        <p>{product.description}</p>

                        <a href=''>Acessar</a>
                    </article>
                ))}
                <div className='actions'>
                    <button onClick={this.prevPage}>Back</button>
                    <button onClick={this.nextPage}>Next</button>
                </div>
            </div>
        )
    }
}
